import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import {
    Container,
    Card,
    Badge,
    Accordion,
    Table,
    Button,
    Row,
    Col,
    Form,
    InputGroup,
    Alert,
    Modal
} from 'react-bootstrap';

const RequestList = ({ data: initialData }) => {
    const [data, setData] = useState(initialData);
    const [searchTerm, setSearchTerm] = useState('');
    const [filterStatus, setFilterStatus] = useState('all');
    const [currentPage, setCurrentPage] = useState(1);
    const [uploadError, setUploadError] = useState(null);
    const [fullscreenContent, setFullscreenContent] = useState(null);
    const [showFullscreen, setShowFullscreen] = useState(false);
    const groupByUrl = true;
    const itemsPerPage = 20;

    // Handle fullscreen modal
    const openFullscreen = (content) => {
        setFullscreenContent(content);
        setShowFullscreen(true);
    };

    const closeFullscreen = () => {
        setShowFullscreen(false);
        setFullscreenContent(null);
    };

    // Pretty print JSON
    const prettyPrintJson = (body) => {
        try {
            const parsed = JSON.parse(body);
            return JSON.stringify(parsed, null, 2);
        } catch (e) {
            return body; // Return as-is if not valid JSON
        }
    };

    // Handle file upload
    const handleFileUpload = (event) => {
        const file = event.target.files[0];
        if (!file) return;

        const reader = new FileReader();
        reader.onload = (e) => {
            try {
                const json = JSON.parse(e.target.result);
                if (json.requests && Array.isArray(json.requests)) {
                    setData(json);
                    setUploadError(null);
                    setCurrentPage(1);
                } else {
                    setUploadError('Invalid JSON format. Expected an object with "requests" array.');
                }
            } catch (err) {
                setUploadError('Failed to parse JSON file: ' + err.message);
            }
        };
        reader.readAsText(file);
    };

    // Filter requests
    const filteredRequests = data?.requests?.filter(req => {
        const matchesSearch =
            req.request.url.toLowerCase().includes(searchTerm.toLowerCase()) ||
            req.request.method.toLowerCase().includes(searchTerm.toLowerCase()) ||
            req.id.toLowerCase().includes(searchTerm.toLowerCase());

        const matchesStatus =
            filterStatus === 'all' ||
            (filterStatus === 'matched' && req.wasMatched) ||
            (filterStatus === 'unmatched' && !req.wasMatched);

        return matchesSearch && matchesStatus;
    }) || [];

    // Group by URL if enabled
    const groupedRequests = groupByUrl
        ? Object.entries(
            filteredRequests.reduce((acc, req) => {
                const url = req.request.url;
                if (!acc[url]) {
                    acc[url] = [];
                }
                acc[url].push(req);
                return acc;
            }, {})
        ).map(([url, requests]) => ({
            url,
            requests,
            count: requests.length
        }))
        : filteredRequests.map(req => ({ url: req.request.url, requests: [req], count: 1 }));

    // Pagination
    const totalPages = Math.ceil(groupedRequests.length / itemsPerPage);
    const startIndex = (currentPage - 1) * itemsPerPage;
    const paginatedRequests = groupedRequests.slice(startIndex, startIndex + itemsPerPage);

    // Format date
    const formatDate = (dateString) => {
        return new Date(dateString).toLocaleString();
    };

    // Get method badge color
    const getMethodColor = (method) => {
        const colors = {
            'GET': 'primary',
            'POST': 'success',
            'PUT': 'warning',
            'DELETE': 'danger',
            'PATCH': 'info'
        };
        return colors[method] || 'secondary';
    };

    // Get status badge color
    const getStatusColor = (status) => {
        if (status >= 200 && status < 300) return 'success';
        if (status >= 300 && status < 400) return 'info';
        if (status >= 400 && status < 500) return 'warning';
        if (status >= 500) return 'danger';
        return 'secondary';
    };

    // Render request details (extracted for reuse)
    const renderRequestDetails = (req) => (
        <>
            {/* Request Details */}
            <Card className="mb-2">
                <Card.Header className="bg-light py-1">
                    <strong>📤 Request Details</strong>
                </Card.Header>
                <Card.Body className="py-2">
                    <Row>
                        <Col>
                            <p className="mb-1">
                                <strong style={{ paddingRight: '8px' }}>ID:</strong> <code>{req.id}</code>{' '}
                                <strong style={{ paddingLeft: '12px', paddingRight: '8px' }}>Absolute URL:</strong> <code>{req.request.absoluteUrl}</code>{' '}
                                <strong style={{ paddingLeft: '12px', paddingRight: '8px' }}>Client IP:</strong> <code>{req.request.clientIp}</code>{' '}
                                <strong style={{ paddingLeft: '12px', paddingRight: '8px' }}>Protocol:</strong> <code>{req.request.protocol}</code>{' '}
                                <strong style={{ paddingLeft: '12px', paddingRight: '8px' }}>Host:</strong> <code>{req.request.host}:{req.request.port}</code>
                            </p>
                        </Col>
                    </Row>

                    {/* Request Headers */}
                    <div className="mt-2">
                        <strong>📋 Headers</strong>
                        <Badge bg="info" className="ms-2">
                            {Object.keys(req.request.headers).length}
                        </Badge>
                        <Table striped bordered hover size="sm" responsive className="mt-2">
                            <thead>
                                <tr>
                                    <th>Header</th>
                                    <th>Value</th>
                                </tr>
                            </thead>
                            <tbody>
                                {Object.entries(req.request.headers).map(([key, value]) => (
                                    <tr key={key}>
                                        <td><strong>{key}</strong></td>
                                        <td className="text-break">
                                            <code>{value}</code>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </Table>
                    </div>

                    {/* Request Body */}
                    {req.request.body && (
                        <Accordion className="mt-2">
                            <Accordion.Item eventKey="request-body">
                                <Accordion.Header>
                                    <strong>📄 Request Body</strong>
                                </Accordion.Header>
                                <Accordion.Body className="py-2">
                                    <pre className="bg-light p-2 rounded mb-0" style={{ maxHeight: '300px', overflow: 'auto' }}>
                                        <code>{req.request.body}</code>
                                    </pre>
                                </Accordion.Body>
                            </Accordion.Item>
                        </Accordion>
                    )}
                </Card.Body>
            </Card>

            {/* Response Details */}
            <Card className="mb-2">
                <Card.Header className="bg-light py-1">
                    <strong>📥 Response Details</strong>
                </Card.Header>
                <Card.Body className="py-2">
                    <Row className="mb-2">
                        <Col md={2}>
                            <p className="mb-0">
                                <>Status:</>{' '}
                                <Badge bg={getStatusColor(req.response.status)}>
                                    {req.response.status}
                                </Badge>
                            </p>
                        </Col>
                        <Col md={2}>
                            <p className="mb-0"><>From Configured Stub:</>{' '}
                                {req.responseDefinition.fromConfiguredStub ? '✓ Yes' : '✗ No'}
                            </p>
                        </Col>
                    </Row>
                    {/* Timing Information */}
                    <Row className="mt-2 pt-2 border-top">
                        <Col xs={2}>
                            <strong>⏱️ Timing:</strong>
                        </Col>
                        <Col md={2}>
                            <small><>Process:</> {req.timing.processTime}ms</small>
                        </Col>
                        <Col md={2}>
                            <small><>Serve:</> {req.timing.serveTime}ms</small>
                        </Col>
                        <Col md={2}>
                            <small><>Response Send:</> {req.timing.responseSendTime}ms</small>
                        </Col>
                        <Col md={2}>
                            <small><>Total:</> {req.timing.totalTime}ms</small>
                        </Col>
                    </Row>

                    {/* Response Headers */}
                    {req.response.headers && Object.keys(req.response.headers).length > 0 && (
                        <div className="mt-2">
                            <strong>📋 Response Headers</strong>
                            <Table striped bordered hover size="sm" responsive className="mt-2">
                                <tbody>
                                    {Object.entries(req.response.headers).map(([key, value]) => (
                                        <tr key={key}>
                                            <td><strong>{key}</strong></td>
                                            <td><code>{value}</code></td>
                                        </tr>
                                    ))}
                                </tbody>
                            </Table>
                        </div>
                    )}

                    {/* Response Body */}
                    {req.response.body && (
                        <Accordion className="mt-2">
                            <Accordion.Item eventKey="response-body">
                                <Accordion.Header>
                                    <strong>📄 Response Body</strong>
                                </Accordion.Header>
                                <Accordion.Body className="py-2">
                                    <div className="d-flex justify-content-end mb-1">
                                        <Button
                                            variant="outline-primary"
                                            size="sm"
                                            onClick={() => openFullscreen(prettyPrintJson(req.response.body))}
                                        >
                                            🖥️ Fullscreen
                                        </Button>
                                    </div>
                                    <pre className="bg-light p-2 rounded mb-0" style={{ maxHeight: '300px', overflow: 'auto' }}>
                                        <code>{prettyPrintJson(req.response.body)}</code>
                                    </pre>
                                </Accordion.Body>
                            </Accordion.Item>
                        </Accordion>
                    )}


                </Card.Body>
            </Card>

            {/* Stub Mapping */}
            {req.stubMapping && (
                <Card className="mb-2">
                    <Card.Header className="bg-light py-1">
                        <strong>🔧 Stub Mapping</strong>
                    </Card.Header>
                    <Card.Body className="py-2">
                        <p className="mb-1"><strong>Stub ID:</strong> <code>{req.stubMapping.id}</code></p>
                        {req.stubMapping.metadata?.name && (
                            <p className="mb-1"><strong>Name:</strong> {req.stubMapping.metadata.name}</p>
                        )}
                        <p className="mb-0"><strong>Persistent:</strong> {req.stubMapping.persistent ? '✓ Yes' : '✗ No'}</p>
                    </Card.Body>
                </Card>
            )}
        </>
    );

    return (
        <Container fluid className="py-4">
            <Row className="mb-4">
                <Col>
                    <h1 className="mb-4">
                        🔍 Request Monitor Dashboard
                        <Badge bg="secondary" className="ms-3">{filteredRequests.length} requests</Badge>
                    </h1>
                </Col>
            </Row>

            {/* File Upload Section */}
            <Row className="mb-4">
                <Col>
                    <Card className="shadow-sm">
                        <Card.Body>
                            <Row className="align-items-center">
                                <Col md={8}>
                                    <Form.Group>
                                        <Form.Label><strong>📁 Upload JSON File</strong></Form.Label>
                                        <Form.Control
                                            type="file"
                                            accept=".json"
                                            onChange={handleFileUpload}
                                        />
                                        <Form.Text className="text-muted">
                                            Upload a JSON file containing request data
                                        </Form.Text>
                                    </Form.Group>
                                </Col>
                                <Col md={4} className="text-end">
                                    <div>
                                        <small className="text-muted">Total Requests:</small>
                                        <h3 className="mb-0">{data?.requests?.length || 0}</h3>
                                    </div>
                                </Col>
                            </Row>
                            {uploadError && (
                                <Alert variant="danger" className="mt-3 mb-0" dismissible onClose={() => setUploadError(null)}>
                                    {uploadError}
                                </Alert>
                            )}
                        </Card.Body>
                    </Card>
                </Col>
            </Row>

            {/* Filter and Search Bar */}
            <Row className="mb-4">
                <Col md={8}>
                    <InputGroup>
                        <InputGroup.Text>
                            🔎
                        </InputGroup.Text>
                        <Form.Control
                            type="text"
                            placeholder="Search by URL, method, or ID..."
                            value={searchTerm}
                            onChange={(e) => {
                                setSearchTerm(e.target.value);
                                setCurrentPage(1);
                            }}
                        />
                        {searchTerm && (
                            <Button variant="outline-secondary" onClick={() => setSearchTerm('')}>
                                Clear
                            </Button>
                        )}
                    </InputGroup>
                </Col>
                <Col md={4}>
                    <Form.Select
                        value={filterStatus}
                        onChange={(e) => {
                            setFilterStatus(e.target.value);
                            setCurrentPage(1);
                        }}
                    >
                        <option value="all">All Requests</option>
                        <option value="matched">✓ Matched Only</option>
                        <option value="unmatched">✗ Unmatched Only</option>
                    </Form.Select>
                </Col>
            </Row>

            {/* Request Cards */}
            {paginatedRequests.length === 0 ? (
                <Alert variant="info">
                    <Alert.Heading>No requests found</Alert.Heading>
                    <p>Try adjusting your search or filter criteria, or upload a new JSON file.</p>
                </Alert>
            ) : (
                <Accordion>
                    {paginatedRequests.map((group, groupIndex) => (
                        <div key={groupIndex}>
                            {groupByUrl && group.count > 1 ? (
                                // Parent collapsible group for multiple requests with same URL
                                <Card className="mb-3 shadow-sm border-primary">
                                    <Accordion.Item eventKey={`group-${groupIndex}`}>
                                        <Accordion.Header>
                                            <Container fluid>
                                                <Row className="w-100 align-items-center">
                                                    <Col xs={12} md={8}>
                                                        <strong>📁 </strong>
                                                        <code className="text-break">{group.url}</code>
                                                    </Col>
                                                    <Col xs={12} md={4} className="text-end">
                                                        <Badge bg="primary" className="me-2">
                                                            {group.count} requests
                                                        </Badge>
                                                        <Badge bg="success">
                                                            {group.requests.filter(r => r.wasMatched).length} matched
                                                        </Badge>
                                                    </Col>
                                                </Row>
                                            </Container>
                                        </Accordion.Header>
                                        <Accordion.Body>
                                            <Accordion>
                                                {group.requests.map((req) => (
                                                    <Card key={req.id} className="mb-2">
                                                        <Accordion.Item eventKey={req.id}>
                                                            <Accordion.Header>
                                                                <Container fluid>
                                                                    <Row className="w-100 align-items-center g-2">
                                                                        <Col xs={12} md={2}>
                                                                            <Badge bg={getMethodColor(req.request.method)}>
                                                                                {req.request.method}
                                                                            </Badge>
                                                                        </Col>
                                                                        <Col xs={6} md={3}>
                                                                            <Badge bg={getStatusColor(req.response.status)}>
                                                                                Status: {req.response.status}
                                                                            </Badge>
                                                                        </Col>
                                                                        <Col xs={6} md={3}>
                                                                            {req.wasMatched ? (
                                                                                <Badge bg="success">✓ Matched</Badge>
                                                                            ) : (
                                                                                <Badge bg="danger">✗ Not Matched</Badge>
                                                                            )}
                                                                        </Col>
                                                                        <Col xs={12} md={4}>
                                                                            <small className="text-muted">
                                                                                {formatDate(req.request.loggedDateString)}
                                                                            </small>
                                                                        </Col>
                                                                    </Row>
                                                                </Container>
                                                            </Accordion.Header>
                                                            <Accordion.Body>
                                                                {/* Request details content stays the same */}
                                                                {renderRequestDetails(req)}
                                                            </Accordion.Body>
                                                        </Accordion.Item>
                                                    </Card>
                                                ))}
                                            </Accordion>
                                        </Accordion.Body>
                                    </Accordion.Item>
                                </Card>
                            ) : (
                                // Single request (not grouped or only 1 request for this URL)
                                group.requests.map((req) => (
                                    <Card key={req.id} className="mb-3 shadow-sm">
                                        <Accordion.Item eventKey={req.id}>
                                            <Accordion.Header>
                                                <Container fluid>
                                                    <Row className="w-100 align-items-center g-2">
                                                        <Col xs={12} md={1}>
                                                            <Badge bg={getMethodColor(req.request.method)}>
                                                                {req.request.method}
                                                            </Badge>
                                                        </Col>
                                                        <Col xs={12} md={5}>
                                                            <code className="text-break">{req.request.url}</code>
                                                        </Col>
                                                        <Col xs={6} md={2}>
                                                            <Badge bg={getStatusColor(req.response.status)}>
                                                                Status: {req.response.status}
                                                            </Badge>
                                                        </Col>
                                                        <Col xs={6} md={2}>
                                                            {req.wasMatched ? (
                                                                <Badge bg="success">✓ Matched</Badge>
                                                            ) : (
                                                                <Badge bg="danger">✗ Not Matched</Badge>
                                                            )}
                                                        </Col>
                                                        <Col xs={12} md={2}>
                                                            <small className="text-muted">
                                                                {formatDate(req.request.loggedDateString)}
                                                            </small>
                                                        </Col>
                                                    </Row>
                                                </Container>
                                            </Accordion.Header>

                                            <Accordion.Body>
                                                {renderRequestDetails(req)}
                                            </Accordion.Body>
                                        </Accordion.Item>
                                    </Card>
                                ))
                            )}
                        </div>
                    ))}
                </Accordion>
            )}

            {/* Pagination */}
            {totalPages > 1 && (
                <Row className="mt-4">
                    <Col className="d-flex justify-content-center">
                        <nav>
                            <ul className="pagination">
                                <li className={`page-item ${currentPage === 1 ? 'disabled' : ''}`}>
                                    <Button
                                        variant="outline-primary"
                                        onClick={() => setCurrentPage(prev => Math.max(1, prev - 1))}
                                        disabled={currentPage === 1}
                                    >
                                        ← Previous
                                    </Button>
                                </li>

                                {/* Show page numbers */}
                                {[...Array(Math.min(totalPages, 5))].map((_, i) => {
                                    let pageNum;
                                    if (totalPages <= 5) {
                                        pageNum = i + 1;
                                    } else if (currentPage <= 3) {
                                        pageNum = i + 1;
                                    } else if (currentPage >= totalPages - 2) {
                                        pageNum = totalPages - 4 + i;
                                    } else {
                                        pageNum = currentPage - 2 + i;
                                    }

                                    return (
                                        <li key={pageNum} className="mx-1">
                                            <Button
                                                variant={currentPage === pageNum ? 'primary' : 'outline-primary'}
                                                onClick={() => setCurrentPage(pageNum)}
                                            >
                                                {pageNum}
                                            </Button>
                                        </li>
                                    );
                                })}

                                <li className={`page-item ${currentPage === totalPages ? 'disabled' : ''}`}>
                                    <Button
                                        variant="outline-primary"
                                        onClick={() => setCurrentPage(prev => Math.min(totalPages, prev + 1))}
                                        disabled={currentPage === totalPages}
                                    >
                                        Next →
                                    </Button>
                                </li>
                            </ul>
                        </nav>
                    </Col>
                </Row>
            )}

            {/* Footer Stats */}
            <Row className="mt-4">
                <Col>
                    <Card className="bg-light">
                        <Card.Body>
                            <Row className="text-center">
                                <Col md={3}>
                                    <h5>{filteredRequests.length}</h5>
                                    <small className="text-muted">Total Filtered</small>
                                </Col>
                                <Col md={3}>
                                    <h5>{filteredRequests.filter(r => r.wasMatched).length}</h5>
                                    <small className="text-muted">Matched</small>
                                </Col>
                                <Col md={3}>
                                    <h5>{filteredRequests.filter(r => !r.wasMatched).length}</h5>
                                    <small className="text-muted">Unmatched</small>
                                </Col>
                                <Col md={3}>
                                    <h5>Page {currentPage} of {totalPages}</h5>
                                    <small className="text-muted">Pagination</small>
                                </Col>
                            </Row>
                        </Card.Body>
                    </Card>
                </Col>
            </Row>

            {/* Fullscreen Modal */}
            <Modal
                show={showFullscreen}
                onHide={closeFullscreen}
                size="xl"
                fullscreen="lg-down"
            >
                <Modal.Header closeButton>
                    <Modal.Title>Response Body - Fullscreen View</Modal.Title>
                </Modal.Header>
                <Modal.Body style={{ maxHeight: '80vh', overflow: 'auto' }}>
                    <pre className="bg-light p-3 rounded mb-0" style={{ fontSize: '14px' }}>
                        <code>{fullscreenContent}</code>
                    </pre>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={closeFullscreen}>
                        Close
                    </Button>
                    <Button
                        variant="primary"
                        onClick={() => {
                            navigator.clipboard.writeText(fullscreenContent);
                        }}
                    >
                        📋 Copy to Clipboard
                    </Button>
                </Modal.Footer>
            </Modal>
        </Container>
    );
};

export default RequestList;
