import React, { useState, useEffect } from 'react';
import Cookies from 'js-cookie';
import RequestList from './RequestList';
import MappingsList from './components/MappingsList';
import { Container, Row, Col, ButtonGroup, Button } from 'react-bootstrap';
import './App.css';

function App() {
  const [currentView, setCurrentView] = useState('requests');
  const [selectedWireMockUrl, setSelectedWireMockUrl] = useState(null);

  // Get selected URL from cookie or use default
  useEffect(() => {
    const getInitialUrl = () => {
      const savedUrl = Cookies.get('selectedWireMockUrl');
      if (savedUrl) {
        try {
          return JSON.parse(savedUrl);
        } catch (e) {
          console.error('Error parsing saved URL:', e);
        }
      }
      // Default to first environment variable if available
      const defaultEnv = process.env.REACT_APP_WIREMOCK_URL_1;
      if (defaultEnv) {
        const [name, url] = defaultEnv.split('|');
        return { id: 1, name: name.trim(), url: url.trim() };
      }
      return { id: 1, name: 'Default', url: 'https://wiremock.stage.exite.net/__admin' };
    };

    setSelectedWireMockUrl(getInitialUrl());
  }, []);

  const requestData = {
    "requests": []
  };

  return (
    <div className="App">
      <Container fluid className="py-2">
        {/* View Switcher */}
        <Row className="mb-3">
          <Col className="d-flex justify-content-center">
            <ButtonGroup>
              <Button 
                variant={currentView === 'requests' ? 'primary' : 'outline-primary'}
                onClick={() => setCurrentView('requests')}
              >
                <i className="bi bi-file-earmark-text me-2"></i>
                Requests
              </Button>
              <Button 
                variant={currentView === 'mappings' ? 'primary' : 'outline-primary'}
                onClick={() => setCurrentView('mappings')}
              >
                <i className="bi bi-diagram-3 me-2"></i>
                Mappings
              </Button>
            </ButtonGroup>
          </Col>
        </Row>
      </Container>

      {/* Render appropriate view */}
      {currentView === 'requests' ? (
        <RequestList data={requestData} />
      ) : (
        <MappingsList apiUrl={selectedWireMockUrl?.url} />
      )}
    </div>
  );
}

export default App;
