import React, { useState } from 'react';
import RequestList from './RequestList';
import MappingsList from './components/MappingsList';
import DarkModeToggle from './components/DarkModeToggle';
import { Container, Row, Col, ButtonGroup, Button } from 'react-bootstrap';
import './App.css';

function App() {
  const [currentView, setCurrentView] = useState('requests');

  const requestData = {
    "requests": []
  };

  return (
    <div className="App">
      <Container fluid className="py-2" style={{ marginBottom: '-70px' }}>
        {/* View Switcher */}
        <Row className="mb-3">
          <Col xs={12} md={4} className="d-flex align-items-center justify-content-start mb-2 mb-md-0">
            <DarkModeToggle />
          </Col>
          <Col xs={12} md={4} className="d-flex justify-content-center mb-2 mb-md-0">
            <ButtonGroup>
              <Button 
                variant={currentView === 'requests' ? 'primary' : 'outline-primary'}
                onClick={() => setCurrentView('requests')}
              >
                <i className="bi bi-file-earmark-text me-2"></i>
                Requests
              </Button>
              <Button 
                variant={currentView === 'mappings' ? 'primary' : 'outline-primary'}
                onClick={() => setCurrentView('mappings')}
              >
                <i className="bi bi-diagram-3 me-2"></i>
                Mappings
              </Button>
            </ButtonGroup>
          </Col>
          <Col xs={12} md={4}></Col>
        </Row>
      </Container>

      {/* Render appropriate view */}
      {currentView === 'requests' ? (
        <RequestList data={requestData} />
      ) : (
        <MappingsList />
      )}
    </div>
  );
}

export default App;
